// src/components/Login.js
import React, { useState } from 'react';
import axios from 'axios';
import './Login.css';

const API_URL = 'http://localhost:3000';

function Login({ onLogin }) {
  const [username, setUsername] = useState('');
  const [password, setPassword] = useState('');
  const [error, setError] = useState('');
  const [isSignup, setIsSignup] = useState(false);

  const handleSubmit = async (e) => {
    e.preventDefault();
    try {
      const endpoint = isSignup ? 'signup' : 'login';
      const res = await axios.post(`${API_URL}/auth/${endpoint}`, { username, password });
      onLogin(res.data.access_token);
      setError('');
    } catch (err) {
      setError(isSignup ? 'Signup failed' : 'Invalid credentials');
    }
  };

  return (
    <div className="form-container">
      <p className="title">{isSignup ? 'Sign Up' : 'Welcome back'}</p>
      <form className="form" onSubmit={handleSubmit}>
        <input
          type="text"
          className="input"
          placeholder="Username"
          value={username}
          onChange={(e) => setUsername(e.target.value)}
        />
        <input
          type="password"
          className="input"
          placeholder="Password"
          value={password}
          onChange={(e) => setPassword(e.target.value)}
        />
        <button className="form-btn">{isSignup ? 'Sign Up' : 'Log in'}</button>
      </form>
      {error && <p className="error">{error}</p>}
      <p className="sign-up-label">
        {isSignup ? 'Already have an account?' : 'Don’t have an account?'}
        <span className="sign-up-link" onClick={() => setIsSignup(!isSignup)}>
          {isSignup ? 'Log in' : 'Sign up'}
        </span>
      </p>
    </div>
  );
}

export default Login;
