import React, { useState, useEffect } from 'react';
import axios from 'axios';
import './TaskManager.css';

const API_URL = 'http://localhost:3000';

function TaskManager({ token, isAdmin }) {
  const [tasks, setTasks] = useState([]);
  const [title, setTitle] = useState('');
  const [description, setDescription] = useState('');
  const [status, setStatus] = useState('pending');
  const [file, setFile] = useState(null);

  useEffect(() => {
    fetchTasks();
  }, []);

  const fetchTasks = async () => {
    try {
      const res = await axios.get(`${API_URL}/tasks`, {
        headers: { Authorization: `Bearer ${token}` },
      });
      setTasks(res.data);
    } catch (err) {
      console.error('Error fetching tasks:', err);
    }
  };

  const handleCreate = async (e) => {
    e.preventDefault();
    try {
      await axios.post(
        `${API_URL}/tasks`,
        { title, description, status },
        { headers: { Authorization: `Bearer ${token}` } }
      );
      setTitle('');
      setDescription('');
      setStatus('pending');
      fetchTasks();
    } catch (err) {
      console.error('Error creating task:', err);
    }
  };

  const handleUpdate = async (id, currentStatus) => {
    const newStatus = currentStatus === 'pending' ? 'completed' : 'pending';
    try {
      await axios.put(
        `${API_URL}/tasks/${id}`,
        { status: newStatus },
        { headers: { Authorization: `Bearer ${token}` } }
      );
      fetchTasks();
    } catch (err) {
      console.error('Error updating task:', err);
    }
  };

  const handleDelete = async (id) => {
    try {
      await axios.delete(`${API_URL}/tasks/${id}`, {
        headers: { Authorization: `Bearer ${token}` },
      });
      fetchTasks();
    } catch (err) {
      console.error('Error deleting task:', err);
    }
  };

  const handleUpload = async (e) => {
    e.preventDefault();
    if (!file) return;
    const formData = new FormData();
    formData.append('file', file);
    try {
      await axios.post(`${API_URL}/tasks/upload`, formData, {
        headers: { Authorization: `Bearer ${token}` },
      });
      setFile(null);
      alert('File uploaded successfully');
    } catch (err) {
      console.error('Error uploading file:', err);
    }
  };

  const handleExport = async () => {
    try {
      const res = await axios.get(`${API_URL}/tasks/export`, {
        headers: { Authorization: `Bearer ${token}` },
        responseType: 'blob',
      });
      const url = window.URL.createObjectURL(new Blob([res.data]));
      const link = document.createElement('a');
      link.href = url;
      link.setAttribute('download', 'tasks.csv');
      document.body.appendChild(link);
      link.click();
      document.body.removeChild(link);
    } catch (err) {
      console.error('Error exporting tasks:', err);
    }
  };

  const handleDownloadLogs = async () => {
    try {
      const res = await axios.get(`${API_URL}/tasks/logs`, {
        headers: { Authorization: `Bearer ${token}` },
        responseType: 'blob',
      });
      const url = window.URL.createObjectURL(new Blob([res.data]));
      const link = document.createElement('a');
      link.href = url;
      link.setAttribute('download', 'logs.txt');
      document.body.appendChild(link);
      link.click();
      document.body.removeChild(link);
    } catch (err) {
      console.error('Error downloading logs:', err);
    }
  };

  return (
    <div className="task-manager">
      <h2>Tasks</h2>
      <form onSubmit={handleCreate} className="task-form">
        <input
          value={title}
          onChange={(e) => setTitle(e.target.value)}
          placeholder="Title"
          required
        />
        <input
          value={description}
          onChange={(e) => setDescription(e.target.value)}
          placeholder="Description"
          required
        />
        <select value={status} onChange={(e) => setStatus(e.target.value)}>
          <option value="pending">Pending</option>
          <option value="completed">Completed</option>
        </select>
        <button type="submit">Add Task</button>
      </form>

      <form onSubmit={handleUpload} className="upload-form">
        <input type="file" onChange={(e) => setFile(e.target.files[0])} />
        <button type="submit">Upload File</button>
      </form>

      <button onClick={handleExport}>Download Tasks (CSV)</button>
      {isAdmin && <button onClick={handleDownloadLogs}>Download Logs</button>}

      <div className="task-list">
        {tasks.map((task) => (
          <div key={task.id} className="task">
            <span>{task.title} - {task.description} ({task.status})</span>
            <div>
              <button onClick={() => handleUpdate(task.id, task.status)}>
                Toggle Status
              </button>
              <button onClick={() => handleDelete(task.id)}>Delete</button>
            </div>
          </div>
        ))}
      </div>
    </div>
  );
}

export default TaskManager;
