import React, { useState, useEffect } from 'react';
import axios from 'axios';
import Login from './components/Login';
import TaskManager from './components/TaskManager';
import './App.css';

const API_URL = 'http://localhost:3000';

function App() {
  const [token, setToken] = useState(localStorage.getItem('token') || '');
  const [user, setUser] = useState(null);
  const [isLoading, setIsLoading] = useState(true);

  useEffect(() => {
    if (token) {
      setIsLoading(true);
      axios.get(`${API_URL}/tasks`)
        .then(res => {
          const role = res.data[0]?.userId === 1 ? 'admin' : 'user'; // Assuming userId 1 is admin
          setUser({ role });
          setIsLoading(false);
        })
        .catch(() => {
          setToken('');
          localStorage.removeItem('token');
          setIsLoading(false);
        });
    } else {
      setIsLoading(false);
    }
  }, [token]);

  const handleLogin = (newToken) => {
    setToken(newToken);
    localStorage.setItem('token', newToken);
  };

  const handleLogout = () => {
    setToken('');
    setUser(null);
    localStorage.removeItem('token');
  };

  if (isLoading) {
    return (
      <div className="app-loading">
        <div className="loading-spinner"></div>
        <p>Loading application...</p>
      </div>
    );
  }

  return (
    <div className="app-container">
      {token ? (
        <div className="authenticated-container">
          <nav className="app-navbar">
            <div className="app-brand">Task Manager PRO</div>
            <div className="user-info">
              <span className="user-role">
                <i className="icon-user-circle"></i>
                {user?.role === 'admin' ? 'Administrator' : 'Standard User'}
              </span>
              <button onClick={handleLogout} className="logout-button">
                <i className="icon-logout"></i>
                Logout
              </button>
            </div>
          </nav>
          <main className="app-content">
            <TaskManager token={token} isAdmin={user?.role === 'admin'} />
          </main>
          <footer className="app-footer">
            <p>© 2025 Task Manager PRO. All rights reserved.</p>
          </footer>
        </div>
      ) : (
        <Login onLogin={handleLogin} />
      )}
    </div>
  );
}

export default App;
