import React, { useState } from 'react';
import './Login.css';

function Login() {
  const [email, setEmail] = useState('');
  const [password, setPassword] = useState('');
  const [showPassword, setShowPassword] = useState(false);
  const [emailError, setEmailError] = useState('');
  const [passwordError, setPasswordError] = useState('');
  const [isLoading, setIsLoading] = useState(false);

  const togglePasswordVisibility = () => {
    setShowPassword(!showPassword);
  };

  const userLogin = async (event) => {
    event.preventDefault();
    
    // Reset error messages
    setEmailError('');
    setPasswordError('');
    
    // Simple validation
    let isValid = true;
    
    if (!email) {
      setEmailError('Email is required');
      isValid = false;
    } else if (!/\S+@\S+\.\S+/.test(email)) {
      setEmailError('Please enter a valid email');
      isValid = false;
    }
    
    if (!password) {
      setPasswordError('Password is required');
      isValid = false;
    }
    
    if (isValid) {
      setIsLoading(true);
      
      try {
        // Replace with your actual login API call
        const response = await fetch('/login', {
          method: 'POST',
          headers: {
            'Content-Type': 'application/json',
          },
          body: JSON.stringify({ email, password }),
        });
        
        if (response.ok) {
          window.location.href = '/dashboard'; // Redirect to dashboard on success
        } else {
          const data = await response.json();
          setPasswordError(data.message || 'Login failed');
        }
      } catch (error) {
        setPasswordError('An error occurred. Please try again later.');
      } finally {
        setIsLoading(false);
      }
    }
  };

  return (
    <div className="container">
      <div className="form-container">
        <h2>Welcome Back</h2>
        
        <form id="loginform" onSubmit={userLogin}>
          <div className="form-group">
            <label htmlFor="email">Email</label>
            <input 
              type="email" 
              id="email" 
              name="email" 
              placeholder="Enter email"
              value={email}
              onChange={(e) => setEmail(e.target.value)}
            />
            {emailError && <div id="error1" className="error-message">{emailError}</div>}
          </div>
          
          <div className="form-group">
            <label htmlFor="password">Password</label>
            <div className="password-wrapper">
              <input 
                type={showPassword ? "text" : "password"} 
                id="password" 
                name="password" 
                placeholder="Enter password"
                value={password}
                onChange={(e) => setPassword(e.target.value)}
                required 
              />
              <div className="password-icon" onClick={togglePasswordVisibility}>
                <i className={`fas ${showPassword ? "fa-eye-slash" : "fa-eye"}`}></i>
              </div>
            </div>
            {passwordError && <div id="error2" className="error-message">{passwordError}</div>}
          </div>
          
          <button type="submit" className="btn" disabled={isLoading}>
            {isLoading ? (
              <div className="spinner"></div>
            ) : (
              'Login'
            )}
          </button>
        </form>
        
        <div className="or-continue">or continue with</div>
        
        <a href="/auth/google" className="google-btn">
          Google
        </a>
      </div>
      
      <div className="image-container">
        <img src="./images/person-image.png" alt="Person Image" />
      </div>
    </div>
  );
}

export default Login;
