// src/components/TaskManager.js
import React, { useState, useEffect } from 'react';
import axios from 'axios';
import './TaskManager.css';

const API_URL = 'http://localhost:3000';

function TaskManager({ token, user, onLogout }) {
  const [tasks, setTasks] = useState([]);
  const [title, setTitle] = useState('');
  const [description, setDescription] = useState('');
  const [status, setStatus] = useState('pending');
  const [editId, setEditId] = useState(null);

  useEffect(() => {
    fetchTasks();
  }, []);

  const fetchTasks = async () => {
    try {
      const res = await axios.get(`${API_URL}/tasks`, {
        headers: { Authorization: `Bearer ${token}` },
      });
      setTasks(res.data);
    } catch (err) {
      console.error('Error fetching tasks:', err);
    }
  };

  const handleCreateOrUpdate = async (e) => {
    e.preventDefault();
    try {
      if (editId) {
        await axios.put(
          `${API_URL}/tasks/${editId}`,
          { title, description, status },
          { headers: { Authorization: `Bearer ${token}` } },
        );
        setEditId(null);
      } else {
        await axios.post(
          `${API_URL}/tasks`,
          { title, description, status },
          { headers: { Authorization: `Bearer ${token}` } },
        );
      }
      setTitle('');
      setDescription('');
      setStatus('pending');
      fetchTasks();
    } catch (err) {
      console.error('Error saving task:', err);
    }
  };

  const handleEdit = (task) => {
    setEditId(task._id);
    setTitle(task.title);
    setDescription(task.description);
    setStatus(task.status);
  };

  const handleDelete = async (id) => {
    try {
      await axios.delete(`${API_URL}/tasks/${id}`, {
        headers: { Authorization: `Bearer ${token}` },
      });
      fetchTasks();
    } catch (err) {
      console.error('Error deleting task:', err);
    }
  };

  return (
    <div className="task-manager">
      <div className="header">
        <p>Welcome, {user.username} (Role: {user.role})</p>
        <button onClick={onLogout} className="logout-button">Logout</button>
      </div>
      <h2>{user.role === 'admin' ? 'All Tasks (Admin View)' : 'My Tasks'}</h2>
      <form onSubmit={handleCreateOrUpdate} className="task-form">
        <input value={title} onChange={(e) => setTitle(e.target.value)} placeholder="Title" required className="task-input" />
        <textarea value={description} onChange={(e) => setDescription(e.target.value)} placeholder="Description" required className="task-textarea" />
        <select value={status} onChange={(e) => setStatus(e.target.value)} className="task-select">
          <option value="pending">Pending</option>
          <option value="completed">Completed</option>
        </select>
        <button type="submit" className="task-button">{editId ? 'Update Task' : 'Add Task'}</button>
      </form>
      <div className="task-list">
        {tasks.length === 0 ? (
          <p>No tasks available.</p>
        ) : (
          tasks.map((task) => (
            <div key={task._id} className="task-item">
              <div className="task-details">
                <h3>{task.title}</h3>
                <p>{task.description}</p>
                <p>Status: {task.status}</p>
                {user.role === 'admin' && <p className="admin-info">User ID: {task.userId}</p>}
              </div>
              <div className="task-actions">
                <button onClick={() => handleEdit(task)} className="edit-button">Edit</button>
                <button onClick={() => handleDelete(task._id)} className="delete-button">Delete</button>
              </div>
            </div>
          ))
        )}
      </div>
    </div>
  );
}

export default TaskManager;
