import React, { useState, useEffect } from 'react';
import axios from 'axios';
import Login from './components/Login';
import TaskManager from './components/TaskManager';
import './App.css';

const API_URL = 'http://localhost:3000';

function App() {
  const [token, setToken] = useState(localStorage.getItem('token') || '');
  const [user, setUser] = useState(null);

  useEffect(() => {
    if (token) {
      checkStatus();
    }
  }, [token]);

  const checkStatus = async () => {
    try {
      const res = await axios.get(`${API_URL}/auth/status`, {
        headers: { Authorization: `Bearer ${token}` },
      });
      setUser(res.data); // { userId, username, role }
    } catch (err) {
      setToken('');
      localStorage.removeItem('token');
    }
  };

  const handleLogin = (newToken) => {
    setToken(newToken);
    localStorage.setItem('token', newToken);
  };

  const handleLogout = () => {
    setToken('');
    setUser(null);
    localStorage.removeItem('token');
  };

  return (
    <div className="app">
      <h1>Task Manager PRO</h1>
      {token && user ? (
        <>
          <div className="user-info">Logged in as: {user.username} ({user.role})</div>
          <div className='logout-butto'>
          <button onClick={handleLogout}>Logout</button>
          </div>
          <TaskManager token={token} user={user} />
        </>
      ) : (
        <Login onLogin={handleLogin} />
      )}
    </div>
  );
}

export default App;
