import React, { useState, useEffect } from 'react';
import axios from 'axios';
import Login from './components/Login';
import TaskManager from './components/TaskManager';
import './App.css';

const API_URL = 'http://localhost:3000';

function App() {
  const [token, setToken] = useState(localStorage.getItem('token') || '');
  const [user, setUser] = useState(null);

  useEffect(() => {
    if (token) {
      axios.get(`${API_URL}/tasks`, { headers: { Authorization: `Bearer ${token}` } })
        .then(res => {
          const role = res.data[0]?.userId === 1 ? 'admin' : 'user'; // Assuming userId 1 is admin
          setUser({ role });
        })
        .catch(() => {
          setToken('');
          localStorage.removeItem('token');
        });
    }
  }, [token]);

  const handleLogin = (newToken) => {
    setToken(newToken);
    localStorage.setItem('token', newToken);
  };

  const handleLogout = () => {
    setToken('');
    setUser(null);
    localStorage.removeItem('token');
  };

  return (
    <div className="app">
      <h1>Task Manager PRO</h1>
      {token ? (
        <>
          <div className="user-info">Logged in as: {user?.role}</div>
          <button onClick={handleLogout}>Logout</button>
          <TaskManager token={token} isAdmin={user?.role === 'admin'} />
        </>
      ) : (
        <Login onLogin={handleLogin} />
      )}
    </div>
  );
}

export default App;
