import React, { useState, useEffect } from 'react';
import axios from 'axios';
import './TaskManager.css';

const API_URL = 'http://localhost:3000';

function TaskManager({ token, isAdmin }) {
  const [tasks, setTasks] = useState([]);
  const [title, setTitle] = useState('');
  const [description, setDescription] = useState('');
  const [status, setStatus] = useState('pending');
  const [priority, setPriority] = useState('medium');
  const [dueDate, setDueDate] = useState('');
  const [file, setFile] = useState(null);
  const [isFormVisible, setIsFormVisible] = useState(false);
  const [isLoading, setIsLoading] = useState(false);
  const [filter, setFilter] = useState('all');
  const [searchTerm, setSearchTerm] = useState('');
  const [sortBy, setSortBy] = useState('createdAt');
  const [sortOrder, setSortOrder] = useState('desc');
  const [view, setView] = useState('grid');

  useEffect(() => {
    fetchTasks();
  }, []);

  const fetchTasks = async () => {
    setIsLoading(true);
    try {
      const res = await axios.get(`${API_URL}/tasks`, {
        headers: { Authorization: `Bearer ${token}` },
      });
      setTasks(res.data);
    } catch (err) {
      console.error('Error fetching tasks:', err);
    } finally {
      setIsLoading(false);
    }
  };

  const handleCreate = async (e) => {
    e.preventDefault();
    setIsLoading(true);
    try {
      await axios.post(
        `${API_URL}/tasks/add`,
        { title, description, status, priority, dueDate },
        { headers: { Authorization: `Bearer ${token}` } }
      );
      setTitle('');
      setDescription('');
      setStatus('pending');
      setPriority('medium');
      setDueDate('');
      setIsFormVisible(false);
      fetchTasks();
    } catch (err) {
      console.error('Error creating task:', err);
    } finally {
      setIsLoading(false);
    }
  };

  const handleUpdate = async (id, currentStatus) => {
    const newStatus = currentStatus === 'pending' ? 'completed' : 'pending';
    setIsLoading(true);
    try {
      await axios.put(
        `${API_URL}/tasks/update/${id}`,
        { status: newStatus },
        { headers: { Authorization: `Bearer ${token}` } }
      );
      fetchTasks();
    } catch (err) {
      console.error('Error updating task:', err);
    } finally {
      setIsLoading(false);
    }
  };

  const handleDelete = async (id) => {
    if (!window.confirm('Are you sure you want to delete this task?')) return;
    setIsLoading(true);
    try {
      await axios.delete(`${API_URL}/tasks/delete/${id}`, {
        headers: { Authorization: `Bearer ${token}` },
      });
      fetchTasks();
    } catch (err) {
      console.error('Error deleting task:', err);
    } finally {
      setIsLoading(false);
    }
  };

  const handleExport = async () => {
    setIsLoading(true);
    try {
      const res = await axios.get(`${API_URL}/tasks/export`, {
        headers: { Authorization: `Bearer ${token}` },
        responseType: 'blob',
      });
      const url = window.URL.createObjectURL(new Blob([res.data]));
      const link = document.createElement('a');
      link.href = url;
      link.setAttribute('download', 'tasks.csv');
      document.body.appendChild(link);
      link.click();
      document.body.removeChild(link);
    } catch (err) {
      console.error('Error exporting tasks:', err);
    } finally {
      setIsLoading(false);
    }
  };

  const handleDownloadLogs = async () => {
    setIsLoading(true);
    try {
      const res = await axios.get(`${API_URL}/tasks/logs`, {
        headers: { Authorization: `Bearer ${token}` },
        responseType: 'blob',
      });
      const url = window.URL.createObjectURL(new Blob([res.data]));
      const link = document.createElement('a');
      link.href = url;
      link.setAttribute('download', 'logs.txt');
      document.body.appendChild(link);
      link.click();
      document.body.removeChild(link);
    } catch (err) {
      console.error('Error downloading logs:', err);
    } finally {
      setIsLoading(false);
    }
  };

  const getProgressStats = () => {
    const total = tasks.length;
    const completed = tasks.filter(task => task.status === 'completed').length;
    const percentage = total > 0 ? Math.round((completed / total) * 100) : 0;
    
    return { total, completed, percentage };
  };

  const getPriorityIcon = (priority) => {
    switch(priority) {
      case 'high':
        return 'ğŸ”´';
      case 'medium':
        return 'ğŸŸ ';
      case 'low':
        return 'ğŸŸ¢';
      default:
        return 'âšª';
    }
  };

  const filteredTasks = tasks
    .filter(task => {
      // Status filter
      if (filter !== 'all' && task.status !== filter) return false;
      
      // Search term filter
      if (searchTerm && !task.title.toLowerCase().includes(searchTerm.toLowerCase()) && 
          !task.description.toLowerCase().includes(searchTerm.toLowerCase())) return false;
      
      return true;
    })
    .sort((a, b) => {
      // Sort by selected field
      if (a[sortBy] < b[sortBy]) return sortOrder === 'asc' ? -1 : 1;
      if (a[sortBy] > b[sortBy]) return sortOrder === 'asc' ? 1 : -1;
      return 0;
    });

  const stats = getProgressStats();
  
  const toggleSortOrder = () => {
    setSortOrder(sortOrder === 'asc' ? 'desc' : 'asc');
  };

  return (
    <div className="task-manager">
      <div className="sidebar">
        <div className="app-title">
          <h1>TaskFlow</h1>
          <p>Organize & Accomplish</p>
        </div>
        
        <div className="menu-items">
          <button className="menu-item active">
            <span className="menu-icon">ğŸ“‹</span>
            <span>Tasks</span>
          </button>
          <button className="menu-item">
            <span className="menu-icon">ğŸ“…</span>
            <span>Calendar</span>
          </button>
          <button className="menu-item">
            <span className="menu-icon">ğŸ“Š</span>
            <span>Reports</span>
          </button>
          <button className="menu-item">
            <span className="menu-icon">âš™ï¸</span>
            <span>Settings</span>
          </button>
        </div>
        
        <div className="progress-container">
          <div className="progress-title">
            <h3>Progress</h3>
            <span>{stats.percentage}%</span>
          </div>
          <div className="progress-bar">
            <div className="progress-fill" style={{ width: `${stats.percentage}%` }}></div>
          </div>
          <div className="progress-stats">
            <div className="stat-item">
              <span className="stat-value">{stats.completed}</span>
              <span className="stat-label">Completed</span>
            </div>
            <div className="stat-item">
              <span className="stat-value">{stats.total - stats.completed}</span>
              <span className="stat-label">Pending</span>
            </div>
            <div className="stat-item">
              <span className="stat-value">{stats.total}</span>
              <span className="stat-label">Total</span>
            </div>
          </div>
        </div>
      </div>
      
      <div className="content-area">
        <div className="top-bar">
          <div className="search-container">
            <input
              type="text"
              placeholder="Search tasks..."
              value={searchTerm}
              onChange={(e) => setSearchTerm(e.target.value)}
              className="search-input"
            />
            <button className="search-button">ğŸ”</button>
          </div>
          
          <div className="user-menu">
            <div className="notification-icon">ğŸ””</div>
            <div className="user-avatar">ğŸ‘¤</div>
          </div>
        </div>
        
        <div className="content-header">
          <div className="content-title">
            <h2>My Tasks</h2>
            <p>{filteredTasks.length} items</p>
          </div>
          
          <div className="content-actions">
            <div className="view-toggle">
              <button 
                className={`view-button ${view === 'grid' ? 'active' : ''}`}
                onClick={() => setView('grid')}
              >
                Grid
              </button>
              <button 
                className={`view-button ${view === 'list' ? 'active' : ''}`}
                onClick={() => setView('list')}
              >
                List
              </button>
            </div>
            
            <button className="add-task-button" onClick={() => setIsFormVisible(!isFormVisible)}>
              {isFormVisible ? 'Cancel' : '+ Add Task'}
            </button>
            
            <div className="more-actions">
              <button className="more-button">â€¢â€¢â€¢</button>
              <div className="dropdown-menu">
                <button onClick={handleExport}>
                  Export CSV
                </button>
                {isAdmin && (
                  <button onClick={handleDownloadLogs}>
                    Download Logs
                  </button>
                )}
              </div>
            </div>
          </div>
        </div>
        
        <div className="filter-sort-bar">
          <div className="filter-buttons">
            <button 
              className={`filter-button ${filter === 'all' ? 'active' : ''}`}
              onClick={() => setFilter('all')}
            >
              All
            </button>
            <button 
              className={`filter-button ${filter === 'pending' ? 'active' : ''}`}
              onClick={() => setFilter('pending')}
            >
              Pending
            </button>
            <button 
              className={`filter-button ${filter === 'completed' ? 'active' : ''}`}
              onClick={() => setFilter('completed')}
            >
              Completed
            </button>
          </div>
          
          <div className="sort-options">
            <span>Sort by:</span>
            <select 
              value={sortBy}
              onChange={(e) => setSortBy(e.target.value)}
            >
              <option value="createdAt">Date Created</option>
              <option value="dueDate">Due Date</option>
              <option value="title">Title</option>
              <option value="priority">Priority</option>
            </select>
            <button onClick={toggleSortOrder} className="sort-order-button">
              {sortOrder === 'asc' ? 'â†‘' : 'â†“'}
            </button>
          </div>
        </div>
        
        {isFormVisible && (
          <div className="task-form-container">
            <h2>Create New Task</h2>
            <form onSubmit={handleCreate} className="task-form">
              <div className="form-row">
                <div className="form-group">
                  <label htmlFor="title">Title</label>
                  <input
                    id="title"
                    value={title}
                    onChange={(e) => setTitle(e.target.value)}
                    placeholder="What needs to be done?"
                    required
                  />
                </div>
                <div className="form-group">
                  <label htmlFor="priority">Priority</label>
                  <select 
                    id="priority"
                    value={priority} 
                    onChange={(e) => setPriority(e.target.value)}
                  >
                    <option value="low">Low</option>
                    <option value="medium">Medium</option>
                    <option value="high">High</option>
                  </select>
                </div>
              </div>
              
              <div className="form-row">
                <div className="form-group">
                  <label htmlFor="description">Description</label>
                  <textarea
                    id="description"
                    value={description}
                    onChange={(e) => setDescription(e.target.value)}
                    placeholder="Add details about this task"
                    rows="3"
                    required
                  />
                </div>
              </div>
              
              <div className="form-row">
                <div className="form-group">
                  <label htmlFor="status">Status</label>
                  <select 
                    id="status"
                    value={status} 
                    onChange={(e) => setStatus(e.target.value)}
                  >
                    <option value="pending">Pending</option>
                    <option value="completed">Completed</option>
                  </select>
                </div>
                <div className="form-group">
                  <label htmlFor="dueDate">Due Date</label>
                  <input
                    id="dueDate"
                    type="date"
                    value={dueDate}
                    onChange={(e) => setDueDate(e.target.value)}
                  />
                </div>
              </div>
              
              <div className="form-actions">
                <button type="button" className="cancel-button" onClick={() => setIsFormVisible(false)}>
                  Cancel
                </button>
                <button type="submit" className="save-button" disabled={isLoading}>
                  {isLoading ? 'Saving...' : 'Save Task'}
                </button>
              </div>
            </form>
          </div>
        )}
        
        {isLoading && !filteredTasks.length ? (
          <div className="loading-container">
            <div className="loading-spinner"></div>
            <p>Loading tasks...</p>
          </div>
        ) : (
          <>
            {filteredTasks.length === 0 ? (
              <div className="empty-state">
                <div className="empty-illustration">ğŸ“</div>
                <h3>No tasks found</h3>
                <p>Create a new task to get started!</p>
                <button className="add-task-button" onClick={() => setIsFormVisible(true)}>
                  + Add Task
                </button>
              </div>
            ) : (
              <div className={`task-container ${view}`}>
                {filteredTasks.map((task) => (
                  <div key={task._id} className={`task-card ${task.status}`}>
                    <div className="task-header">
                      <div className="task-priority">
                        {task.priority && getPriorityIcon(task.priority)}
                      </div>
                      <div className="task-actions">
                        <button 
                          onClick={() => handleUpdate(task._id, task.status)}
                          className="action-button toggle"
                          title={task.status === 'pending' ? 'Mark as Completed' : 'Mark as Pending'}
                        >
                          {task.status === 'pending' ? 'âœ“' : 'â†º'}
                        </button>
                        <button 
                          onClick={() => handleDelete(task._id)}
                          className="action-button delete"
                          title="Delete Task"
                        >
                          ğŸ—‘ï¸
                        </button>
                      </div>
                    </div>
                    
                    <div className="task-content">
                      <h3 className="task-title">{task.title}</h3>
                      <p className="task-description">{task.description}</p>
                    </div>
                    
                    <div className="task-footer">
                      <span className={`task-status ${task.status}`}>
                        {task.status.charAt(0).toUpperCase() + task.status.slice(1)}
                      </span>
                      
                      {task.dueDate && (
                        <span className="task-due-date">
                          Due: {new Date(task.dueDate).toLocaleDateString()}
                        </span>
                      )}
                    </div>
                  </div>
                ))}
              </div>
            )}
          </>
        )}
      </div>
    </div>
  );
}

export default TaskManager;
